-- meta
bestiary.__index = bestiary

-- Reserved storages
STORAGE_RANGE_BESTIARY_COUNT = 6520000

function Player.getBestiaryCount(self, id)
    return math.max(self:getStorageValue(STORAGE_RANGE_BESTIARY_COUNT + id, 0))
end

function Player.setBestiaryCount(self, id, value)
    self:setStorageValue(STORAGE_RANGE_BESTIARY_COUNT + id, value)
end

function bestiary:onKill(player, target)
    -- Checks if monster killed is on bestiary
    local bestiaryMonster = bestiary[target:getName()]
    if not bestiaryMonster then
    print("Monster ".. target:getName() .."was not found in bestiary")
        return true    
    end
    -- Sum bestiary count
    local bestiaryId = bestiaryMonster.id

    if player:getBestiaryCount(bestiaryId) == bestiaryFirstUnlock then    
        player:sendTextMessage(MESSAGE_EVENT_DEFAULT, "You unlocked details for the creature " .. target:getName() .. ".")  
        return true    
    end

    if player:getBestiaryCount(bestiaryId) == bestiarySecondUnlock then    
        player:sendTextMessage(MESSAGE_EVENT_DEFAULT, "You unlocked details for the creature " .. target:getName() .. ".")  
        return true    
    end

    if player:getBestiaryCount(bestiaryId) == bestiarytoKill then    
        player:sendTextMessage(MESSAGE_EVENT_DEFAULT, "You unlocked full details for the creature " .. target:getName() .. ".")  
        return true    
    end

    if not bestiaryMonster then
    print("Monster ".. target:getName() .."was not found in bestiary")
        return true    
    end
    -- Sum bestiary count
    player:setBestiaryCount(bestiaryId, player:getBestiaryCount(bestiaryId) + 1)
   -- player:sendTextMessage(MESSAGE_EVENT_DEFAULT, "You unlocked details for the creature " .. target:getName() .. ".")    
   -- player:sendTextMessage(MESSAGE_EVENT_DEFAULT, string.format('You unlocked details for the creature ', player:getBestiaryCount(bestiaryId) + 1, target:getName():lower()))
    --player:sendTextMessage(MESSAGE_EVENT_DEFAULT, string.format('[Bestiary]: You now have killed %d %s.', player:getBestiaryCount(bestiaryId) + 1, target:getName():lower()))
    return true
end